#python2
import os import glob from datetime import datetime as dt
query = '{query}' saveDirectoryPath = '~/Desktop/workDiary/'
def validationPath(path): return os.path.expanduser(path)
def checkDirectory(path,create): path = validationPath(path) if os.path.exists(path) == False : if create : os.makedirs(path)
def todayFileName(): datetime = dt.now() timeString = datetime.strftime('%Y-%m-%d') return timeString + '.txt'
def checkTodayWorkDiaryFile(path,create) : path = validationPath(path) todayFile = None if os.path.isfile(path) == False: if create : todayFile = open(path,'w') todayFile.write('') todayFile.close()
def todayWorkDiaryFilePath(): return saveDirectoryPath + todayFileName()
def writeTimestamp(query): checkDirectory(saveDirectoryPath,True)
todayFilePath = todayWorkDiaryFilePath() checkTodayWorkDiaryFile(todayFilePath,True)
todayFile = open (validationPath(todayFilePath),'a')
datetime = dt.now() timeString = datetime.strftime('%H%:%M')
writeString = '- '+timeString + ' ' + query
todayFile.write(writeString+'\n\n') todayFile.close() #postNotification('WorkDiary','Write to today WorkDiary')
def sendDayOneTodayFile(): checkDirectory(saveDirectoryPath,True) todayFilePath = todayWorkDiaryFilePath() checkTodayWorkDiaryFile(todayFilePath,True) print todayFilePath os.system('/usr/local/bin/dayone new < ' + todayFilePath) #postNotification('WorkDiary','Send To DayOne')
def postNotification(title,description): script = 'echo ' + "'display notification "+ '"' + description + '"' + "with title "+ '"' + title + '"' + "' | osascript" os.system(script)
if query == '/send' : print query sendDayOneTodayFile() else: print query writeTimestamp(query)
